import { useRef, useState } from "react";

export default function Wheel() {
  const circleRef = useRef(null);
  const [currentValue, setCurrentValue] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const startAngleRef = useRef(null);

  const getAngle = (x, y, cx, cy) => {
    return Math.atan2(y - cy, x - cx) * (180 / Math.PI);
  };

  const handleMouseDown = (e) => {
    setIsDragging(true);
    const rect = circleRef.current.getBoundingClientRect();
    startAngleRef.current = getAngle(
      e.clientX,
      e.clientY,
      rect.left + rect.width / 2,
      rect.top + rect.height / 2,
    );
  };

  const handleMouseMove = (e) => {
    if (!isDragging) {
      return;
    }
    const rect = circleRef.current.getBoundingClientRect();
    const currentAngle = getAngle(
      e.clientX,
      e.clientY,
      rect.left + rect.width / 2,
      rect.top + rect.height / 2,
    );
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    startAngleRef.current = null;
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    const rect = circleRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    startAngleRef.current = getAngle(
      touch.clientX,
      touch.clientY,
      rect.left + rect.width / 2,
      rect.top + rect.height / 2,
    );
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const rect = circleRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const currentAngle = getAngle(
      touch.clientX,
      touch.clientY,
      rect.left + rect.width / 2,
      rect.top + rect.height / 2,
    );

    if (startAngleRef !== null) {
      const deltaAngle = currentAngle - startAngleRef.current;
      if (deltaAngle > 5) {
        setCurrentValue((prev) => prev + 1);
      } else if (deltaAngle < -5) {
        setCurrentValue((prev) => prev + 1);
        startAngleRef.current = currentAngle;
      }
    }
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
    startAngleRef.current = null;
  };

  return (
    <>
      <div
        id="circle"
        ref={circleRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
      >
        <span id="number">0</span>
      </div>
    </>
  );
}
